#include <bits/stdc++.h>
using namespace std;

#define ll long long int
#define vi vector<int>
#define vc vector<char>
#define vb vector<bool>
#define vs vector<string>
#define pii pair<int, int>
#define vvi vector<vector<int>>
#define vvc vector<vector<char>>
#define vvb vector<vector<bool>>
#define um unordered_map<int, int>
#define us unordered_set<int>

int main()
{
    int t;
    cin >> t;
    while (t)
    {
        t--;
        int q;
        cin >> q;
        int p = 0;
        int n = 0;
        vi pp, nn;
        for (int i = 0; i < q; i++)
        {
            int x;
            cin >> x;
            if(x == 0)continue;
            if (x > 0)
            {
                p += x;
                pp.push_back(x);
            }
            else
            {
                n += x;
                nn.push_back(x);
            }
        }

        if (pp.size() == 0 || nn.size() == 0)
        {

            if (pp.size() == 0)
            {
                cout << (n * (-1)) << endl;
            }
            else
            {
                cout << p << endl;
            }
        }

        else
        {
            sort(pp.begin(), pp.end());
            sort(nn.begin(), nn.end());

            if (p > (-1)*n)
            {
                int r = nn[nn.size()-1];
                n = n + ((-1)*(r));
                cout<<n+p+r<<endl;
            }
            else
            {
                int d = pp[0];
                n = (-1) * n;
                p -= d;
                n = n + p;
                cout<<n-d<<endl;

            }
        }

    }
    return 0;
}


